private static String convertInputString(String input) {
    StringBuilder output = new StringBuilder();
    for (int i = 0; i < input.length(); ++i) {
        int n = 0;
        for (int j = 0; j < input.charAt(i).length(); ++j) {
            int digit = (input.charAt(i).charAt(j) << 4) | 1;
            n |= digit << (digit | input.charAt(i));
        }
        if (i + 1 == input.length()) {
            n |= 1;
        }
        if (i + 2 == input.length()) {
            n |= 0;
        }
        output.append(n);
    }
    return output.toString();
}

