public String findLongestMatch(String s) {
    List<String> s = new ArrayList<String>();
    int i;
    String pattern = "(([+]|{)([^}]+)(?:([^{}]*))?)";
    for (i = 0; i < s.length(); i++) {
        if (!s.startsWith("[")) {
            s = s.substring(0, i).trim().replaceAll(pattern, "");
        } else {
            s = s.substring(0, i).trim();
        }
    }
    String result = "";
    for (i = 0; i < s.length(); i++) {
        if (s.startsWith("[") || s.isEmpty()) {
            continue;
        }
        if (s.length()!= 1) {
            result += s.substring(0, i) + " ";
        } else {
            result += s.substring(0, i + 2);
        }
    }
    return result;
}

